// Dhivehi sentiment words (negative)
const DHIVEHI_NEGATIVE = new Set([
    'ނުރަނގަޅު', 'ނުފިނި', 'ނުކުރިއަރު', 'ނުފުރިހަމަ', 'ދަތިވަރުން', 'ދަތި', 'މުޝްކިލަ', 'މުޝްކިލަވަރުން', 'ނުފަސޭހަ', 'ނުރަނގަޅުކަމަެ', 'ކުޑަ', 'މަދު', 'ނައިސް'
]);

/**
 * Extract top keywords from an array of comments
 */
export function extractKeywords(comments, topN = 10) {
    const wordFreq = {};
    
    comments.forEach(comment => {
        if (!comment) return;
        const words = comment.toLowerCase()
            .replace(/[^\w\s]/g, ' ')
            .split(/\s+/)
            .filter(w => w.length > 2 && !STOP_WORDS.has(w));
        
        words.forEach(word => {
            wordFreq[word] = (wordFreq[word] || 0) + 1;
        });
    });
    
    return Object.entries(wordFreq)
        .sort((a, b) => b[1] - a[1])
        .slice(0, topN)
        .map(([word, count]) => ({ word, count }));
}

/**
 * Analyze sentiment of comments
 */
export function analyzeSentiment(comments) {
    let positive = 0;
    let negative = 0;
    let neutral = 0;
    
    comments.forEach(comment => {
        if (!comment) {
            neutral++;
            return;
        }
        
        const words = comment.toLowerCase().split(/\s+/);
        let posCount = 0;
        let negCount = 0;
        
        words.forEach(word => {
            if (POSITIVE_WORDS.has(word)) posCount++;
            if (NEGATIVE_WORDS.has(word)) negCount++;
            if (DHIVEHI_POSITIVE.has(word)) posCount++;
            if (DHIVEHI_NEGATIVE.has(word)) negCount++;
        });
        
        if (posCount > negCount) positive++;
        else if (negCount > posCount) negative++;
        else neutral++;
    });
    
    const total = comments.length || 1;
    return {
        positive: Math.round((positive / total) * 100),
        negative: Math.round((negative / total) * 100),
        neutral: Math.round((neutral / total) * 100),
        counts: { positive, negative, neutral }
    };
}

/**
 * Categorize comments by topic
 */
export function categorizeComments(comments) {
    const categories = {};
    
    // Initialize categories
    Object.keys(TOPIC_CATEGORIES).forEach(cat => {
        categories[cat] = [];
    });
    categories['Other'] = [];
    
    comments.forEach((comment, index) => {
        if (!comment) {
            categories['Other'].push(index);
            return;
        }
        
        const words = comment.toLowerCase().split(/\s+/);
        let matched = false;
        
        for (const [category, keywords] of Object.entries(TOPIC_CATEGORIES)) {
            if (keywords.some(kw => words.some(w => w.includes(kw)))) {
                categories[category].push(index);
                matched = true;
                break;
            }
        }
        
        if (!matched) {
            categories['Other'].push(index);
        }
    });
    
    return categories;
}

/**
 * Generate word frequency data for word cloud
 */
export function generateWordCloudData(comments) {
    const wordFreq = {};
    
    comments.forEach(comment => {
        if (!comment) return;
        const words = comment.toLowerCase()
            .replace(/[^\w\s]/g, ' ')
            .split(/\s+/)
            .filter(w => w.length > 3 && !STOP_WORDS.has(w));
        
        words.forEach(word => {
            wordFreq[word] = (wordFreq[word] || 0) + 1;
        });
    });
    
    return Object.entries(wordFreq)
        .map(([text, value]) => ({ text, value }))
        .sort((a, b) => b.value - a.value)
        .slice(0, 50);
}

/**
 * Calculate statistics for comments
 */
export function calculateStats(comments) {
    const total = comments.length;
    const withComments = comments.filter(c => c && c.trim()).length;
    const empty = total - withComments;
    
    const lengths = comments
        .filter(c => c)
        .map(c => c.length);
    
    const avgLength = lengths.length > 0 
        ? Math.round(lengths.reduce((a, b) => a + b, 0) / lengths.length)
        : 0;
    
    const longest = lengths.length > 0 ? Math.max(...lengths) : 0;
    const shortest = lengths.length > 0 ? Math.min(...lengths) : 0;
    
    return {
        total,
        withComments,
        empty,
        responseRate: Math.round((withComments / total) * 100),
        avgLength,
        longest,
        shortest
    };
}

/**
 * Extract common phrases (2-3 words)
 */
export function extractPhrases(comments, topN = 10) {
    const phraseFreq = {};
    
    comments.forEach(comment => {
        if (!comment) return;
        const words = comment.toLowerCase()
            .replace(/[^\w\s]/g, ' ')
            .split(/\s+/)
            .filter(w => w.length > 2 && !STOP_WORDS.has(w));
        
        // Extract 2-word phrases
        for (let i = 0; i < words.length - 1; i++) {
            const phrase = words[i] + ' ' + words[i + 1];
            phraseFreq[phrase] = (phraseFreq[phrase] || 0) + 1;
        }
    });
    
    return Object.entries(phraseFreq)
        .filter(([phrase, count]) => count > 1)
        .sort((a, b) => b[1] - a[1])
        .slice(0, topN)
        .map(([phrase, count]) => ({ phrase, count }));
}

/**
 * Generate complete summary
 */
export function generateCommentSummary(comments) {
    return {
        stats: calculateStats(comments),
        keywords: extractKeywords(comments, 15),
        phrases: extractPhrases(comments, 10),
        sentiment: analyzeSentiment(comments),
        categories: categorizeComments(comments),
        wordCloudData: generateWordCloudData(comments)
    };
}
